pragma solidity ^0.4.24;

import 'zeppelin-solidity/contracts/math/SafeMath.sol';
import 'zeppelin-solidity/contracts/lifecycle/Destructible.sol';

contract Store is Destructible {

    using SafeMath for uint;

    address public owner;
    bytes32 public storeName;
    uint256 public balance;

    mapping(uint => Product) public products;

    /* Events */
    event NewProductAdded(uint productId);
    event ProductQuantityAdded(uint productId, uint remainingStock);
    event ProductRemoved(uint productId, uint remainingStock );

    event ProductQuantityAvailable(uint productId, uint quantity);
    event ProductPurchaseSuccessful(uint productId, uint remainingStock);
    event ProductPurchaseFailed(uint productId, uint remainingStock);


    struct Product {
        uint id;
        bytes32 name;
        uint price;
        uint stock;
    }

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    constructor(address _owner, bytes32 _storeName) public {
        owner = _owner;
        storeName = _storeName;
    }

    function getStoreName() public view returns (bytes32) {
      return storeName;
    }

    function addNewProduct(uint id, bytes32 name, uint price, uint stock) public onlyOwner returns (bool success) {
        Product memory newProduct = Product(id, name, price, stock);
        if (isProductValid(newProduct)) {
            products[id] = newProduct;
            emit NewProductAdded(id);
            return true;
        }
        return false;
    }

    function addProductStock(uint id, uint quantity) public onlyOwner returns (bool success) {
        uint actualStock = products[id].stock;
        products[id].stock = actualStock.add(quantity);
        emit ProductQuantityAdded(id, products[id].stock);
        return true;
    }

    function removeProduct(uint id, uint quantity) public onlyOwner returns (bool success){
        Product memory productToRemove = products[id];
        if (productToRemove.id == id) {
            uint actualStock = productToRemove.stock;
            productToRemove.stock = actualStock.sub(quantity);
            emit ProductRemoved(id, productToRemove.stock);
            return true;
        }
        return false;
    }

    function getProduct(uint id) public view returns (bytes32 name, uint price, uint stock) {
        return (products[id].name,
                products[id].price,
                products[id].stock);
    }

    function checkProductAvailability(uint id, uint quantity) public returns(bool available) {
        if (products[id].stock >= quantity) {
            emit ProductQuantityAvailable(id, quantity);
            return true;
        }
        return false;
    }

    function payProduct(uint id, uint quantity) public payable returns (bool success) {
        Product memory productToBuy = products[id];
        uint totalPrice = productToBuy.price.mul(quantity);
        if (msg.value >= totalPrice && productToBuy.stock >= quantity) {
            balance.add(msg.value);
            productToBuy.stock.sub(quantity);
            emit ProductPurchaseSuccessful(id, productToBuy.stock);
            return true;
        }
        return false;
    }

    function isProductValid(Product product) private pure returns (bool isValid) {
        return (product.price > 0 && product.name != "");
    }

    function getStoreBalance() public onlyOwner view returns (uint256) {
        return balance;
    }

    // Fallback function - Called if other functions don't match call or
    // sent ether without data
    // Typically, called when invalid data is sent
    // Added so ether sent to this contract is reverted if the contract fails
    // otherwise, the sender's money is transferred to contract
    function() public payable {
        revert();
    }

}
